class Solution:
	def threeSumMulti(self, A, target: int) -> int:
		MOD = 10**9 + 7

		n = 0
		count = [0] * 101

		for num in A:
			count[num] += 1

		for x in range(101):
			# x,x,y
			z = target - (x*2)

			if x < z < 101:
				n += (count[x] * (count[x]-1) / 2) * count[z]
				n %= MOD

			# x,y,y
			z = target - x
			if z%2 == 0:
				z = int(z/2)
				if x < z < 101:
					n += count[x] * (count[z] * (count[z]-1) / 2)
					n %= MOD

			# x,y,z
			for y in range(x+1, 101):
				z = target - x - y

				if y < z < 101:
					n += count[x] * count[y] * count[z]
					n %= MOD

		# x,x,x
		if target % 3 == 0:
			z = int(target / 3)
			if 0 <= z < 101:
				n += count[z] * (count[z]-1) * (count[z]-2) / 6

		
		return int(n) % ((10**9)+7)

