class Solution:
	def threeSumMulti(self, A, target: int) -> int:
		n = 0
		for i in range(len(A)):
			for j in range(i+1, len(A)):
				for k in range(j+1, len(A)):
					if A[i]+A[j]+A[k] == target:
						# print("(%02d,%02d,%02d) : (%02d,%02d,%02d)" % (i,j,k, A[i],A[j],A[k]) )
						n += 1
		
		return n % ((10**9)+7)

